mapper.update:
UPDATE CLUB SET
MEMBERCOUNT = MEMBERCOUNT + 1
WHERE CLUBNO = #{clubNo}

UPDATE CLUB SET
MEMBERCOUNT = MEMBERCOUNT - 1
WHERE CLUBNO = #{clubNo}

mapper.select:
SELECT * FROM CLUB
where clubTitle like '%' || #{clubTitle} || '%'

	AND city = #{city}


	AND district = #{district}


	AND CLUBGENDER = #{clubGender}


	AND REGULARTYPE = #{regularType}


	AND CATEGORY = #{category}
